import React, { useState } from 'react';
import styles from './CardList.module.css';
import Preloader from '../common/Preloader/Preloader';

const CardList = ({cats}) => {
  const [count, setCount] = useState(false)
  const [favorites, setFavorites] = useState([]);

  const Card = ({ cats }) => {

    const addToFavorites = cats => {
      let favorites = localStorage.getItem('favorites-cats')

      if (favorites) {
        favorites = JSON.parse(favorites);
        localStorage.setItem('favorites-cats', [])
      }
    }

    return (
      <div className={styles.Wrapper}>
        <img className={styles.Img} key={cats.key} src={cats.url} alt="cats" />
        <div className={styles.ButtonBox}>
          <button onClick={addToFavorites}>add</button>
        </div>
      </div>
    );
  }

  const allCats = cats.map((i) => {
    return (
      <Card cats={i} key={i.id}/>
    );
  })

  return (
    <div className={styles.Container}>
      {allCats}
    </div>

  );
}

export default CardList;
