import React, { useState } from 'react';
import styles from './CardList.module.css';
import Preloader from '../common/Preloader/Preloader';

const CardList = ({cats}) => {
  const [favorites, setFavorites] = useState([]);

  const toggleToFavorites = cats => {
    let cat = [...favorites, cats]

    localStorage.setItem('favorites-cats', JSON.stringify(cat))
    setFavorites(cat);
    alert(`${JSON.stringify(cats.id)} теперь в избранном!`)

  }
  const Card = ({ cats, toggleToFavorites }) => {
    return (
      <div className={styles.Wrapper}>
        <img className={styles.Img} key={cats.key} src={cats.url} alt="cats" />
        <div className={styles.ButtonBox}>
          <button onClick={() => toggleToFavorites(cats)}>add</button>
        </div>
      </div>
    );
  }

  const allCats = cats.map((i) => {
    return (
      <Card cats={i} key={i.id} toggleToFavorites={toggleToFavorites} />
    );
  })

  return (
    <div className={styles.Container}>
      {allCats}
    </div>

  );
}

export default CardList;
