import React from 'react';
import CardList from './../../component/CardList/CardList';
import Preloader from './../../component/common/Preloader/Preloader';

const Homepage = ({ cats, isLoading, addToFavorites }) => {


  const catsFavorites = JSON.parse(localStorage.getItem('favorites-cats'));

  useEffect(() => {
    if (catsFavorites || catsFavorites.length === 0 ) {
      setCats(catsFavorites);
    }
  }, [])
  return (
    <>
      {isLoading ?
        <Preloader>Загружаю котиков...</Preloader>
        :
        <section>
          <CardList
            cats={cats}
            addToFavorites={addToFavorites}
          />
          {!isLoading && <Preloader>Загружаю еще больше котиков...</Preloader>}
        </section>
      }
    </>
  );
}

export default Homepage;
